    set resp.http.x-dd-type = urldecode(req.http.x-datadome-headers-pairs:x-dd-type);
  }
  # Allow Session Feature
  if (resp.http.x-datadome-cookie) {
    add resp.http.set-cookie = resp.http.x-datadome-cookie;
    if (req.http.x-datadome-clientid) {
