vcl.template:
set var.fastly_req_do_shield = (req.restarts <= 1);
set req.http.Fastly-Force-Shield = "1";
if (req.restarts == 0) {
  call cleanup_netacea_variables;
}
call netacea_check_req;

if (!is_path_ignored()) {
  declare local var.netacea_use_relative_path_captcha_assets STRING;
  declare local var.integration_mode STRING;
  set var.netacea_use_relative_path_captcha_assets = get_netacea_config_use_relative_path_captcha_assets();
  if (var.netacea_use_relative_path_captcha_assets == "true") {
    if (std.prefixof(req.url.path, "/Mitigations/") && req.method == "GET") {
      if (std.suffixof(req.url.path, ".css") || std.suffixof(req.url.path, ".js")) {
        return(deliver);
      }
    }
  }
  if (req.http.netacea_captcha_path == "1") {
    call netacea_hide_response_headers;
    call set_netacea_captcha_header;
    return(deliver);
  }
  call netacea_calculate_best_mitigation;
  if (req.http.netacea_processed == "1") {
    set var.integration_mode = get_netacea_config_integration_mode();
    set req.http.mit_status = resp.status;
    if (resp.status != 200 && req.http.X-Netacea-Compile-JSON != "done") {
      set req.http.netacea_best_mitigation = "";
      set req.http.netacea_bctype_string = "";
      if(req.url == "/AtaVerifyCaptcha") {
        unset resp.http.Set-Cookie;
        set req.http.netacea_set_cookies = "0";
      }
    }
    if (var.integration_mode == "MITIGATE" && req.http.X-Netacea-Compile-JSON != "done" && netacea_should_return_json()) { 
      if (req.url != "/AtaVerifyCaptcha" && resp.http.X-Netacea-Mitigate == "1") { 
        if (resp.http.X-Netacea-Captcha == "1" || resp.http.X-Netacea-Captcha == "5") { 
          set req.http.X-Netacea-Event-ID = resp.http.X-Netacea-Event-ID;
          set req.http.X-Netacea-Compile-JSON = "requested";
        }
      }
    }
    if (var.integration_mode == "INJECT" || req.http.X-Netacea-Compile-JSON == "requested") {
      set req.http.X-Netacea-Match = "" resp.http.X-Netacea-Match;
      set req.http.X-Netacea-Mitigate = "" resp.http.X-Netacea-Mitigate;
      set req.http.X-Netacea-Captcha = "" resp.http.X-Netacea-Captcha;
    }
    set req.http.netacea_mitata_captcha_cookie_value = resp.http.x-netacea-mitatacaptcha-value;
    set req.http.netacea_mitata_captcha_cookie_expiry = resp.http.x-netacea-mitatacaptcha-expiry;
    call set_netacea_cookies;
    call netacea_hide_response_headers;
    if(req.url == "/AtaVerifyCaptcha") {
      return(deliver);
    }
    set req.http.host = req.http.netacea_origin_host;
    set req.url = req.http.netacea_origin_url;
    set req.method = req.http.netacea_origin_method;
    unset req.http.netacea_origin_url;
    unset req.http.netacea_origin_host;
    unset req.http.netacea_origin_method;
    unset req.http.X-Netacea-Api-Key;
    if (
      req.http.netacea_best_mitigation != "captcha"
      || var.integration_mode == "INJECT"
      || req.http.X-Netacea-Compile-JSON == "requested"
    ) {
      unset req.http.x-netacea:netacea_check_req_called;
      restart;
    }
    set resp.status = 403;
    set resp.response = "Forbidden";
    unset req.http.X-Netacea-Compile-JSON;
    call set_netacea_captcha_header;
    return(deliver);
  }
  if (fastly.ff.visits_this_service == 0) {
    call set_netacea_cookies;
  }
}

declare local var.captcha_url STRING;
declare local var.response STRING;
if (obj.status == 601 && req.http.X-Netacea-Compile-JSON == "processing") {
  set var.captcha_url = get_netacea_captcha_path();
  set var.captcha_url = var.captcha_url "?trackingId=" req.http.X-Netacea-Event-ID;
  set var.response = "{" LF
                      "  %22captchaRelativeURL%22:%22" var.captcha_url "%22," LF
                      "  %22captchaAbsoluteURL%22:%22" req.protocol "://" fastly_info.host_header var.captcha_url "%22" LF
                      "}";
  set obj.status = 200;
  set obj.response = "Forbidden";
  set obj.http.Content-Type = "application/json";
  set obj.http.X-Netacea-Match = req.http.X-Netacea-Match;
  set obj.http.X-Netacea-Mitigate = req.http.X-Netacea-Mitigate;
  set obj.http.X-Netacea-Captcha = req.http.X-Netacea-Captcha;
  synthetic var.response;
  unset req.http.X-Netacea-Event-ID;
  unset req.http.X-Netacea-Match;
  unset req.http.X-Netacea-Mitigate;
  unset req.http.X-Netacea-Captcha;
  set req.http.X-Netacea-Compile-JSON = "done";
  return(deliver);
}

