  set var.path = regsub(var.path, "^\s*/*", "/");
  set var.path = regsub(var.path, "\s*$", "");
  return urldecode(var.path);
}
sub get_sanitised_netacea_config_captcha_header STRING {
  declare local var.value STRING;
  set var.value = get_netacea_config_captcha_header();
  set var.value = urldecode(regsuball(var.value, "&#x(.{2});", "%25\1"));
  set var.value = std.replaceall(var.value, "&quot;", "%22");
  set var.value = std.replaceall(var.value, "&lt;", "<");
  set var.value = std.replaceall(var.value, "&gt;", ">");
  set var.value = std.replaceall(var.value, "&amp;", "&");
  return var.value;
}
