sub is_path_ignored BOOL {
  declare local var.netacea_ignore_list STRING;
  declare local var.req_path STRING;
  set var.netacea_ignore_list = get_netacea_config_ignore_list();
  set var.req_path = urldecode(req.url.path);
  set var.netacea_ignore_list = regsuball(var.netacea_ignore_list, "\s*(,\s*)+", ",");
  set var.netacea_ignore_list = regsuball(var.netacea_ignore_list, "(^|,)\s*/*", "\1/");
  set var.netacea_ignore_list = regsuball(var.netacea_ignore_list, "/*\s*(,|$)", "\1");
  if (var.netacea_ignore_list ~ "^([^,]+),*([^,]+)?,*([^,]+)?,*([^,]+)?,*([^,]+)?") {
    if (re.group.1 && (var.req_path == urldecode(re.group.1) || std.prefixof(var.req_path, urldecode(re.group.1) + "/"))) {
      return true;
    }
    if (re.group.2 && (var.req_path == urldecode(re.group.2) || std.prefixof(var.req_path, urldecode(re.group.2) + "/"))) {
      return true;
    }
    if (re.group.3 && (var.req_path == urldecode(re.group.3) || std.prefixof(var.req_path, urldecode(re.group.3) + "/"))) {
      return true;
    }
    if (re.group.4 && (var.req_path == urldecode(re.group.4) || std.prefixof(var.req_path, urldecode(re.group.4) + "/"))) {
      return true;
    }
    if (re.group.5 && (var.req_path == urldecode(re.group.5) || std.prefixof(var.req_path, urldecode(re.group.5) + "/"))) {
      return true;
    }
  }
  return false;
}
sub netacea_should_return_json BOOL {
  declare local var.enable_captcha_content_negotiation STRING;
  set var.enable_captcha_content_negotiation = get_netacea_config_enable_captcha_content_negotiation();
  if (var.enable_captcha_content_negotiation != "true") {
    return false;
  }
  declare local var.html_weight FLOAT;
  declare local var.json_weight FLOAT;
  set var.html_weight = 0.0;
  set var.json_weight = 0.0;
  if (req.http.Accept ~ "(?i)(^|,)\s*application/json\s*(;\s*q\s*=\s*(\d*(\.\d+)?|\d+\.)\s*)?(,|$)") {
    set var.json_weight = std.atof(if(re.group.3, re.group.3, "1.0"));
  } elseif (req.http.Accept ~ "(?i)(^|,)\s*application/\*\s*(;\s*q\s*=\s*(\d*(\.\d+)?|\d+\.)\s*)?(,|$)") {
    set var.json_weight = std.atof(if(re.group.3, re.group.3, "1.0"));
  }
  if (req.http.Accept ~ "(?i)(^|,)\s*text/html\s*(;\s*q\s*=\s*(\d*(\.\d+)?|\d+\.)\s*)?(,|$)") {
    set var.html_weight = std.atof(if(re.group.3, re.group.3, "1.0"));
  } elseif (req.http.Accept ~ "(^|,)\s*text/\*\s*(;\s*q\s*=\s*(\d*(\.\d+)?|\d+\.)\s*)?(,|$)") {
    set var.html_weight = std.atof(if(re.group.3, re.group.3, "1.0"));
  }
  return var.json_weight > var.html_weight;
}
