sub set_netacea_cookies {
  if (req.http.netacea_set_cookies == "1") {
    declare local var.ignored BOOL;
    declare local var.netacea_mitSvc_secret STRING;
    declare local var.netacea_encryption_key STRING;
    set var.ignored = setcookie.delete_by_name(resp, "_mitata");
    set var.ignored = setcookie.delete_by_name(resp, "_mitatacaptcha");
    declare local var.netacea_captcha_cookie_name STRING;
    set var.netacea_captcha_cookie_name = get_sanitised_netacea_config_captcha_cookie_name();
    set var.netacea_mitSvc_secret = get_netacea_config_secret_key();
    set var.netacea_encryption_key = get_netacea_config_encryption_key();
    call set_mitata_cookie;
    if (req.http.netacea_mitata_captcha_cookie_value && req.http.netacea_mitata_captcha_cookie_expiry) {
      if (var.netacea_encryption_key ~ ".") {
        declare local var.netacea_iv STRING;
        declare local var.netacea_iv_trimmed STRING;
        declare local var.netacea_sig STRING;
        declare local var.netacea_mitata_captcha_cookie_value_base64 STRING;
        declare local var.netacea_mitata_captcha_cookie_value_hex STRING;
        declare local var.netacea_mitata_captcha_cookie_value_encrypted STRING;
        declare local var.netacea_mitata_captcha_cookie_final_value STRING;
        set var.netacea_mitata_captcha_cookie_value_base64 = digest.base64(req.http.netacea_mitata_captcha_cookie_value);
        set var.netacea_mitata_captcha_cookie_value_hex = bin.base64_to_hex(var.netacea_mitata_captcha_cookie_value_base64);
        set var.netacea_iv = uuid.version4();
        set var.netacea_iv_trimmed = std.replaceall(var.netacea_iv, "-", "");
        set var.netacea_mitata_captcha_cookie_value_encrypted = crypto.encrypt_hex(aes256, ctr, nopad, var.netacea_encryption_key, var.netacea_iv_trimmed, var.netacea_mitata_captcha_cookie_value_hex);
        set var.netacea_sig = digest.hmac_sha256(var.netacea_mitSvc_secret, var.netacea_mitata_captcha_cookie_value_encrypted);
        set var.netacea_mitata_captcha_cookie_final_value = var.netacea_iv_trimmed + "." + var.netacea_mitata_captcha_cookie_value_encrypted + "." + var.netacea_sig;
        add resp.http.Set-Cookie = var.netacea_captcha_cookie_name + "=" + var.netacea_mitata_captcha_cookie_final_value + "; Max-Age=" + req.http.netacea_mitata_captcha_cookie_expiry + "; Path=/;";
      }
      if (var.netacea_encryption_key !~ ".") {
        add resp.http.Set-Cookie = var.netacea_captcha_cookie_name + "=" + req.http.netacea_mitata_captcha_cookie_value + "; Max-Age=" + req.http.netacea_mitata_captcha_cookie_expiry + "; Path=/;";
      }
    }
  }
}
sub netacea_calculate_best_mitigation {
  if (!req.http.netacea_bctype_string) {
    declare local var.netacea_match STRING;
    declare local var.netacea_mitigate STRING;
    declare local var.netacea_captcha STRING;
    declare local var.netacea_match_string STRING;
    declare local var.netacea_mitigate_string STRING;
    declare local var.netacea_captcha_string STRING;
    declare local var.netacea_captcha_mitigate_string STRING;
    declare local var.netacea_best_mitigation STRING;
    declare local var.netacea_bctype_string STRING;
    if (resp.http.x-netacea-match) { 
      set var.netacea_match = resp.http.x-netacea-match;
    } elseif (req.http.netacea_match) { 
      set var.netacea_match = req.http.netacea_match;
    } else {
      set var.netacea_match = "0";
    }
    if (resp.http.x-netacea-mitigate) { 
      set var.netacea_mitigate = resp.http.x-netacea-mitigate;
    } elseif (req.http.netacea_mitigate) { 
      set var.netacea_mitigate = req.http.netacea_mitigate;
    } else {
      set var.netacea_mitigate = "0";
    }
    if (resp.http.x-netacea-captcha) { 
      set var.netacea_captcha = resp.http.x-netacea-captcha;
    } elseif (req.http.netacea_captcha) { 
      set var.netacea_captcha = req.http.netacea_captcha;
    } else {
      set var.netacea_captcha = "0";
    }
    if (var.netacea_match) {
      set var.netacea_match_string = table.lookup(Netacea_Match_Dict, var.netacea_match, "unknown");
      if (var.netacea_match_string != "") {
        set var.netacea_bctype_string = var.netacea_match_string + "_";
      }
    }
    if (var.netacea_mitigate) {
      set var.netacea_mitigate_string = table.lookup(Netacea_Mitigate_Dict, var.netacea_mitigate, "unknown");
      if (var.netacea_mitigate_string != "") {
        set var.netacea_bctype_string = var.netacea_bctype_string + var.netacea_mitigate_string;
      }
      set var.netacea_best_mitigation = table.lookup(Netacea_Best_Mitigations_Dict, var.netacea_mitigate, "no-best-mitigation");
      if (var.netacea_best_mitigation == "no-best-mitigation") {
        set var.netacea_best_mitigation = "";
      }
    }
    if (var.netacea_captcha) {
      if (req.url != "/AtaVerifyCaptcha") {
        if (var.netacea_captcha == "2") {
          set var.netacea_captcha = "4";
        } elseif (var.netacea_captcha == "3") {
          set var.netacea_captcha = "5";
        }
      }
      set var.netacea_captcha_string = table.lookup(Netacea_Captcha_Dict, var.netacea_captcha, "unknown");
      if (var.netacea_captcha_string != "") {
        set var.netacea_bctype_string = var.netacea_bctype_string + "," + var.netacea_captcha_string;
      }
      set var.netacea_captcha_mitigate_string = table.lookup(Netacea_Best_Mitigations_Captcha_Dict, var.netacea_captcha, "no-best-captcha-mitigation");
      if (var.netacea_captcha_mitigate_string != "no-best-captcha-mitigation") {
        set var.netacea_best_mitigation = var.netacea_captcha_mitigate_string;
      }
    }
    set req.http.netacea_bctype_string = var.netacea_bctype_string;
    set req.http.netacea_best_mitigation = var.netacea_best_mitigation;
    set req.http.netacea_best_mitigation_code = var.netacea_match + var.netacea_mitigate + var.netacea_captcha;
    if (var.netacea_mitigate == "3") {
      set req.http.netacea_require_revalidation = "1";
    }
    if (var.netacea_mitigate == "1" && var.netacea_captcha != "2" && var.netacea_captcha != "4") {
      set req.http.netacea_require_revalidation = "1";
    }
  }
}
sub set_mitata_cookie {
    declare local var.netacea_mitSvc_secret STRING;
    declare local var.netacea_encryption_key STRING;
    set var.netacea_mitSvc_secret = get_netacea_config_secret_key();
    set var.netacea_encryption_key = get_netacea_config_encryption_key();
    if (!req.http.X-Netacea-UserId) {
      set req.http.X-Netacea-UserId = "c" + randomstr(15, "1234567890abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ");
    }
    declare local var.netacea_iv STRING;
    declare local var.netacea_iv_trimmed STRING;
    declare local var.netacea_sig STRING;
    declare local var.netacea_mitata_cookie_full_value_base64 STRING;
    declare local var.netacea_mitata_cookie_full_value_hex STRING;
    declare local var.netacea_mitata_cookie_full_value_encrypted STRING;
    declare local var.netacea_mitata_cookie_final_value STRING;
    declare local var.netacea_mitata_cookie_full_value STRING;
    declare local var.netacea_ataCookie_stringValue STRING;
    declare local var.netacea_ataCookie_HMAC STRING;
    declare local var.netacea_mitSvc_exp STRING;
    declare local var.netacea_mitSvc_sig STRING;
    declare local var.netacea_mitSvc_userId STRING;
    declare local var.netacea_mitigation_code STRING;
    declare local var.netacea_client_ip_time_ua STRING;
    declare local var.netacea_client_ip_time_ua_hash STRING;
    declare local var.netacea_cookie_name STRING;
    set var.netacea_mitigation_code = req.http.netacea_best_mitigation_code;
    set var.netacea_mitSvc_userId = req.http.X-Netacea-UserId;
    set var.netacea_cookie_name = get_sanitised_netacea_config_cookie_name();
    if (req.http.netacea_require_revalidation == "1") {
      set var.netacea_mitSvc_exp = time.units("s", time.sub(now, 1m));
    } else {
      set var.netacea_mitSvc_exp = time.units("s", time.add(now, 1m));
    }
    set var.netacea_client_ip_time_ua = req.http.X-Netacea-Client-IP + "|" + var.netacea_mitSvc_exp + "|" +  req.http.user-agent;
    set var.netacea_client_ip_time_ua_hash = digest.hmac_sha256(var.netacea_mitSvc_secret, var.netacea_client_ip_time_ua);
    if (var.netacea_client_ip_time_ua_hash ~ "0x(.*)") {
      set var.netacea_client_ip_time_ua_hash = re.group.1;
    }
    set var.netacea_ataCookie_stringValue = var.netacea_mitSvc_exp + "_/@#/" + var.netacea_mitSvc_userId + "_/@#/" + digest.base64(var.netacea_client_ip_time_ua_hash) + "_/@#/" + var.netacea_mitigation_code;
    set var.netacea_ataCookie_HMAC = digest.hmac_sha256(var.netacea_mitSvc_secret, var.netacea_ataCookie_stringValue);
    if (var.netacea_ataCookie_HMAC ~ "0x(.*)") {
      set var.netacea_ataCookie_HMAC = re.group.1;
    }
    set var.netacea_mitSvc_sig = digest.base64(var.netacea_ataCookie_HMAC);
    set var.netacea_mitata_cookie_full_value = var.netacea_mitSvc_sig + "_/@#/" + var.netacea_ataCookie_stringValue;
    if (var.netacea_encryption_key ~ ".") {
      set var.netacea_mitata_cookie_full_value_base64 = digest.base64(var.netacea_mitata_cookie_full_value);
      set var.netacea_mitata_cookie_full_value_hex = bin.base64_to_hex(var.netacea_mitata_cookie_full_value_base64);
      set var.netacea_iv = uuid.version4();
      set var.netacea_iv_trimmed = std.replaceall(var.netacea_iv, "-", "");
      set var.netacea_mitata_cookie_full_value_encrypted = crypto.encrypt_hex(aes256, ctr, nopad, var.netacea_encryption_key, var.netacea_iv_trimmed, var.netacea_mitata_cookie_full_value_hex);
      set var.netacea_sig = digest.hmac_sha256(var.netacea_mitSvc_secret, var.netacea_mitata_cookie_full_value_encrypted);
      set var.netacea_mitata_cookie_final_value = var.netacea_iv_trimmed + "." + var.netacea_mitata_cookie_full_value_encrypted + "." + var.netacea_sig;
      add resp.http.Set-Cookie = var.netacea_cookie_name + "=" + var.netacea_mitata_cookie_final_value + "; Max-Age=" + time.units("s", 1d) + "; Path=/;";
    }
    if (var.netacea_encryption_key !~ ".") {
      add resp.http.Set-Cookie = var.netacea_cookie_name + "=" + var.netacea_mitata_cookie_full_value+ "; Max-Age=" + time.units("s", 1d) + "; Path=/;";
    }
    set req.http.mitigation_user_id = var.netacea_mitSvc_userId;
}
sub process_netacea_mitata_cookie {
  declare local var.netacea_mitSvc_secret STRING;
  set var.netacea_mitSvc_secret = get_netacea_config_secret_key();
  declare local var.netacea_cookie_sig STRING;
  declare local var.netacea_cookie_payload STRING;
  declare local var.netacea_cookie_expiry STRING;
  declare local var.netacea_client_ip_time_ua_hash STRING;
  declare local var.netacea_real_client_ip_time_ua STRING;
  declare local var.netacea_real_client_ip_time_ua_hash STRING;
  declare local var.netacea_cookie_HMAC STRING;
  declare local var.netacea_cookie_real_value STRING;
  if (req.http.Cookie:_mitata) {
    if (req.http.Cookie:_mitata ~ "^(.*)_\/@#\/((\d+)_\/@#\/(.+)_\/@#\/(.+)_\/@#\/((\d)(\d)(\d)))$") {
      set var.netacea_cookie_sig = re.group.1;
      set var.netacea_cookie_payload = re.group.2;
      set var.netacea_cookie_expiry = re.group.3;
      set req.http.X-Netacea-UserId = re.group.4;
      set var.netacea_client_ip_time_ua_hash = re.group.5;
      set req.http.netacea_match = re.group.7;
      set req.http.netacea_mitigate = re.group.8;
      set req.http.netacea_captcha = re.group.9;
      set var.netacea_cookie_real_value = var.netacea_cookie_expiry + "_/@#/" +  req.http.X-Netacea-UserId + "_/@#/" + var.netacea_client_ip_time_ua_hash + "_/@#/" + req.http.netacea_match + req.http.netacea_mitigate + req.http.netacea_captcha;
      set var.netacea_cookie_HMAC = digest.hmac_sha256(var.netacea_mitSvc_secret, var.netacea_cookie_real_value);
      if (var.netacea_cookie_HMAC ~ "0x(.*)") {
        set var.netacea_cookie_HMAC = re.group.1;
      }
      set var.netacea_real_client_ip_time_ua = req.http.X-Netacea-Client-IP + "|" + var.netacea_cookie_expiry + "|" + req.http.user-agent;
      set var.netacea_real_client_ip_time_ua_hash = digest.hmac_sha256(var.netacea_mitSvc_secret, var.netacea_real_client_ip_time_ua);
      if (var.netacea_real_client_ip_time_ua_hash ~ "0x(.*)") {
        set var.netacea_real_client_ip_time_ua_hash = re.group.1;
      }
      if (var.netacea_cookie_sig != digest.base64(var.netacea_cookie_HMAC)) {
        unset req.http.Cookie:_mitata;
        unset req.http.X-Netacea-UserId;
        unset req.http.netacea_match;
        unset req.http.netacea_mitigate;
        unset req.http.netacea_captcha;
      } else {
        if (time.is_after(now, std.time(var.netacea_cookie_expiry, now)) || digest.base64(var.netacea_real_client_ip_time_ua_hash) != var.netacea_client_ip_time_ua_hash ) {
          set req.http.netacea_mitata_must_reauthenticate = "1";
        }
      }
    } else {
      unset req.http.Cookie:_mitata;
    }
  }
  if (!req.http.Cookie:_mitata) {
    unset req.http.Cookie:_mitatacaptcha;
  }
}
sub normalise_netacea_cookie_names {
  declare local var.netacea_custom_cookie_name STRING;
  declare local var.netacea_custom_captcha_cookie_name STRING;
  set var.netacea_custom_cookie_name = get_sanitised_netacea_config_cookie_name();
  set var.netacea_custom_captcha_cookie_name = get_sanitised_netacea_config_captcha_cookie_name();
  set req.http.Cookie = regsuball(req.http.Cookie, ";\s*+", "; ");
  if (var.netacea_custom_cookie_name !~ "^_mitata$") {
    unset req.http.Cookie:_mitata;
    set req.http.Cookie = std.replace_prefix(req.http.Cookie, var.netacea_custom_cookie_name + "=", "_mitata=");
    set req.http.Cookie = std.replace(req.http.Cookie, "; " + var.netacea_custom_cookie_name + "=", "; _mitata=");
  }
  if (var.netacea_custom_captcha_cookie_name !~ "^_mitatacaptcha$") {
    unset req.http.Cookie:_mitatacaptcha;
    set req.http.Cookie = std.replace_prefix(req.http.Cookie, var.netacea_custom_captcha_cookie_name + "=", "_mitatacaptcha=");
    set req.http.Cookie = std.replace(req.http.Cookie, "; " + var.netacea_custom_captcha_cookie_name + "=", "; _mitatacaptcha=");
  }
}
sub decrypt_netacea_cookies_values {
  declare local var.netacea_mitSvc_secret STRING;
  declare local var.netacea_mitata_cookie_encrypted STRING;
  declare local var.netacea_encryption_key STRING;
  declare local var.netacea_iv STRING;
  declare local var.netacea_mitata_cookie_base64 STRING;
  declare local var.netacea_mitata_cookie_hex STRING;
  declare local var.netacea_mitata_cookie_value STRING;
  declare local var.netacea_mitata_cookie_sig STRING;
  declare local var.netacea_mitata_captcha_cookie_encrypted STRING;
  declare local var.netacea_captcha_iv STRING;
  declare local var.netacea_mitata_captcha_cookie_base64 STRING;
  declare local var.netacea_mitata_captcha_cookie_hex STRING;
  declare local var.netacea_mitata_captcha_cookie_value STRING;
  declare local var.netacea_mitata_captcha_cookie_sig STRING;
  set var.netacea_encryption_key = get_netacea_config_encryption_key();
  if (var.netacea_encryption_key ~ ".") {
    set var.netacea_mitSvc_secret = get_netacea_config_secret_key();
    if (req.http.Cookie:_mitata ~ ".") {
      if (req.http.Cookie:_mitata ~ "^(.*?)\.") {
        set var.netacea_iv = re.group.1;
      }
      if (req.http.Cookie:_mitata ~ "\.(.*?)\.") {
        set var.netacea_mitata_cookie_encrypted = re.group.1;
      }
      if (req.http.Cookie:_mitata ~ "([^\.]+$)") {
        set var.netacea_mitata_cookie_sig  = re.group.1;
      }
      set var.netacea_mitata_cookie_hex = crypto.decrypt_hex(aes256, ctr, nopad, var.netacea_encryption_key, var.netacea_iv, var.netacea_mitata_cookie_encrypted);
      set var.netacea_mitata_cookie_base64 = bin.hex_to_base64(var.netacea_mitata_cookie_hex);
      set var.netacea_mitata_cookie_value = digest.base64_decode(var.netacea_mitata_cookie_base64);
      set req.http.Cookie:_mitata = var.netacea_mitata_cookie_value;
      if(var.netacea_mitata_cookie_sig != digest.hmac_sha256(var.netacea_mitSvc_secret, var.netacea_mitata_cookie_encrypted)) {
        unset req.http.Cookie:_mitata;
      }
    }
    if (req.http.Cookie:_mitatacaptcha ~ "^(.*?)\.") {
      if (req.http.Cookie:_mitatacaptcha ~ "^(.*?)\.") {
        set var.netacea_captcha_iv = re.group.1;
      }
      if (req.http.Cookie:_mitatacaptcha ~ "\.(.*?)\.") {
        set var.netacea_mitata_captcha_cookie_encrypted = re.group.1;
      }
      if (req.http.Cookie:_mitatacaptcha ~ "([^\.]+$)") {
        set var.netacea_mitata_captcha_cookie_sig  = re.group.1;
      }
      set var.netacea_mitata_captcha_cookie_hex = crypto.decrypt_hex(aes256, ctr, nopad, var.netacea_encryption_key, var.netacea_captcha_iv, var.netacea_mitata_captcha_cookie_encrypted);
      set var.netacea_mitata_captcha_cookie_base64 = bin.hex_to_base64(var.netacea_mitata_captcha_cookie_hex);
      set var.netacea_mitata_captcha_cookie_value = digest.base64_decode(var.netacea_mitata_captcha_cookie_base64);
      set req.http.Cookie:_mitatacaptcha = var.netacea_mitata_captcha_cookie_value;
      if(var.netacea_mitata_captcha_cookie_sig != digest.hmac_sha256(var.netacea_mitSvc_secret, var.netacea_mitata_captcha_cookie_encrypted)) {
        unset req.http.Cookie:_mitatacaptcha;
      }
    }
  }
}
