sub netacea_check_req {
  declare local var.netacea_mitSvc_authenticate BOOL;
  declare local var.netacea_mitSvc_apiKey STRING;
  declare local var.netacea_integration_mode STRING;
  declare local var.netacea_use_relative_path_captcha_assets STRING;
  declare local var.captcha_path STRING;
  if (req.http.x-netacea:netacea_check_req_called || fastly.ff.visits_this_service > 0) {
    return;
  }
  set var.netacea_integration_mode = get_netacea_config_integration_mode();
  if (std.strlen(var.netacea_integration_mode) == 0) {
    return;
  }
  set req.http.x-netacea:netacea_check_req_called = "true";
  unset req.http.netacea_processed;
  if (is_path_ignored()) {
    return;
  }
  set var.netacea_mitSvc_apiKey = get_netacea_config_api_key();
  set var.netacea_use_relative_path_captcha_assets = get_netacea_config_use_relative_path_captcha_assets();
  set var.captcha_path = get_netacea_captcha_path();
  if (var.netacea_use_relative_path_captcha_assets == "true") {
    if (std.prefixof(req.url.path, "/Mitigations/") && req.method == "GET") {
      if (std.suffixof(req.url.path, ".css") || std.suffixof(req.url.path, ".js")) {
        set req.backend = F_CaptchaAssets;
        return(lookup);
      }
    }
  }
  if (var.captcha_path != "" && urldecode(req.url.path) == var.captcha_path) {
    set req.backend = F_MitSvc;
    if (req.backend.healthy) {
      set req.http.netacea_origin_method = "GET";
      set req.http.netacea_processed = "1";
      set req.http.netacea_captcha_path = "1";
      set req.http.netacea_origin_host = req.http.host;
      set req.http.netacea_origin_url = req.url;
      set req.url = "/captcha?" + req.url.qs;
      set req.http.X-Netacea-Api-Key = var.netacea_mitSvc_apiKey;
      return(lookup);
    }
  }
  if (req.restarts == 0) {
    call set_netacea_ip_header;
    if (var.netacea_integration_mode != "BYPASS") {
      set var.netacea_mitSvc_authenticate = true;
      call normalise_netacea_cookie_names;
      call decrypt_netacea_cookies_values;
      call process_netacea_mitata_cookie;
    }
  } else {
    if (req.http.X-Netacea-Compile-JSON == "requested") {
      set req.http.netacea_processed = "1";
      set req.http.X-Netacea-Compile-JSON = "processing";
      error 601;
    }
    if (var.netacea_integration_mode == "MITIGATE" && req.http.netacea_best_mitigation == "block") {
      error 403;
    }
  }
  if (req.http.Cookie:_mitata && !req.http.netacea_mitata_must_reauthenticate) {
    set var.netacea_mitSvc_authenticate = false;
  }
  set req.http.mitigation_user_id = req.http.X-Netacea-UserId;
  set req.http.integration_type = get_netacea_config_integration_type();
  set req.http.integration_version = get_netacea_config_integration_version();
  if (var.netacea_mitSvc_authenticate) {
    set req.http.netacea_set_cookies = "1";
  } else {
    if (var.netacea_integration_mode == "INJECT" && req.restarts == 0) {
      set req.http.X-Netacea-Match = req.http.netacea_match;
      set req.http.X-Netacea-Mitigate = req.http.netacea_mitigate;
      set req.http.X-Netacea-Captcha = req.http.netacea_captcha;
    }
  }
  if (var.netacea_integration_mode ~ "(MITIGATE|INJECT)" && var.netacea_mitSvc_authenticate) {
    set req.backend = F_MitSvc;
    if (req.backend.healthy) {
      unset req.http.netacea_match;
      unset req.http.netacea_mitigate;
      unset req.http.netacea_captcha;
      unset req.http.Cookie:_mitata;
      set req.http.netacea_origin_method = req.method;
      set req.http.netacea_processed = "1";
      set req.http.netacea_origin_host = req.http.host;
      set req.http.netacea_origin_url = req.url;
      if (req.url != "/AtaVerifyCaptcha") {
        set req.method = "GET";
        set req.url = "/";
      }
      set req.http.X-Netacea-Api-Key = var.netacea_mitSvc_apiKey;
      return(pass);
    }
  }
}
